package javatutorial;

public class switchStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day = 7;
		switch (day){
		case 1:
			System.out.println("A");
			break;
		case 2 : 
			System.out.println("B");
			break;
		case 3 : 
			System.out.println("C");
			break;
		case 4 : 
			System.out.println("D");
			break;
		
		}

	}

}
